import React, { useState } from 'react';

export default function QuizDisplay({ quizData }) {
  const [selectedAnswers, setSelectedAnswers] = useState({}); // { questionIndex: answerIndex }

  if (!quizData) return null;

  const handleSelect = (qIndex, aIndex) => {
    setSelectedAnswers((prev) => ({ ...prev, [qIndex]: aIndex }));
  };

  return (
    <div className="space-y-6">
      {quizData.questions.map((q, qIndex) => (
        <div key={qIndex} className="p-4 border rounded-lg bg-gray-50">
          <p className="font-semibold mb-2">{qIndex + 1}. {q.question}</p>
          <div className="grid gap-2">
            {q.options.map((opt, aIndex) => {
              const isSelected = selectedAnswers[qIndex] === aIndex;
              return (
                <button
                  key={aIndex}
                  onClick={() => handleSelect(qIndex, aIndex)}
                  className={`text-left p-2 border rounded hover:bg-indigo-100 transition ${
                    isSelected ? 'bg-indigo-600 text-white' : 'bg-white text-gray-800'
                  }`}
                >
                  {opt}
                </button>
              );
            })}
          </div>
        </div>
      ))}
    </div>
  );
}
