// frontend/src/components/QuizDisplay.jsx
import React, { useState } from 'react';

export default function QuizDisplay({ quizData }) {
  const [selectedAnswers, setSelectedAnswers] = useState({}); // { questionIndex: answerIndex }
  const [showResults, setShowResults] = useState(false);

  if (!quizData) return null;

  const handleSelect = (qIndex, aIndex) => {
    if (showResults) return; // disable selection after results
    setSelectedAnswers((prev) => ({ ...prev, [qIndex]: aIndex }));
  };

  const handleSubmit = () => {
    setShowResults(true);
  };

  return (
    <div className="space-y-6 max-w-3xl mx-auto">
      {quizData.questions.map((q, qIndex) => (
        <div key={qIndex} className="p-4 border rounded-lg bg-gray-50">
          <p className="font-semibold mb-2">{qIndex + 1}. {q.question}</p>
          <div className="grid gap-2">
            {q.options.map((opt, aIndex) => {
              const isSelected = selectedAnswers[qIndex] === aIndex;
              const isCorrect = showResults && aIndex === q.answer; // assuming q.answer is correct index

              let bgClass = "bg-white text-gray-800 hover:bg-indigo-100";
              if (isSelected) bgClass = "bg-indigo-600 text-white";
              if (showResults && isCorrect) bgClass = "bg-green-500 text-white";
              if (showResults && isSelected && !isCorrect) bgClass = "bg-red-500 text-white";

              return (
                <button
                  key={aIndex}
                  onClick={() => handleSelect(qIndex, aIndex)}
                  className={`text-left p-2 border rounded transition ${bgClass}`}
                >
                  {opt}
                </button>
              );
            })}
          </div>
        </div>
      ))}

      {!showResults && (
        <div className="text-center">
          <button
            onClick={handleSubmit}
            disabled={Object.keys(selectedAnswers).length !== quizData.questions.length}
            className="mt-4 px-6 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 disabled:opacity-50 transition"
          >
            Submit Quiz
          </button>
        </div>
      )}
    </div>
  );
}
